        codMuestra: 'XXXX-AAAA',
        fechaRecepcionMuestra: 'AAAA-MM-DD; hh:mm',
        fechaInforme: Date.now(),
        person: {  
            nombre: "fdjfdjfd",
            cc: "rhere",
            direccion: "rururhv",
            ciudad: "ruruhv",
            departamento: "rueruehv",
            contacto: "reuerueh",
            telefono: "erueueuv",
            correo: "rueehfd",
            id: "eruerueudfh",
        },
        muestra: { 
