            /* let fecha = r.split('T')[0].replace(/-/g, "/")
            console.log(fecha); */
            let d = new Date(r);
            return d.toLocaleDateString() + '-' + d.toLocaleTimeString();
        },
        fechaMuestra(f) {
            let fecha = f.split('T')[0].replace(/-/g, "/")
            return fecha
        },
